    return b'<!DOCTYPE HTML PUBLIC "-//W3C//DTD HTML 4.0 Transitional//EN">\r\n<html>\r\n<head>\r\n<title>SAQA</title>\r\n<link rel=STYLESHEET TYPE="text/css" href="styles.css" title="Normal Style">\r\n</head>\r\n\r\n\t<table border=0 width="100%">\r\n\t<tr><td align=left class=mainmenu>\r\n[<a class="main" href="index.php">Registered Qual &amp; Unit Std Home page</a>]\t\t\t[<a class="main" href="search.php?cat=qual">Search Qualifications</a>]\r\n\t\t\t[<a class="main" href="search.php?cat=unit">Search Unit Standards</a>]\r\n\t</td></tr></table>\r\n\t<table style="width:95%;margin-left: auto; margin-right:auto">\r\n\t<tr>\r\n\t\t<td>\r\n\t\t\t<img src="images/logo.gif" width="136" height="135" alt="SAQA">\t\t</td>\r\n\t\t<td>\r\n\t\t\t<span class="desci">All qualifications and part qualifications registered on the National Qualifications Framework are public property. Thus the only payment that can be made for them is for service and reproduction. It is illegal to sell this material for profit. If the material is reproduced or quoted, the South African Qualifications Authority (SAQA) should be acknowledged as the source.</span>\t\t\t\r\n\t\t</td>\r\n\t</tr>\r\n</table>\r\n\t<table width="90%" align="center" border="0">\r\n\t\t<tr>\r\n\t\t\t<td align="center"><b>SOUTH AFRICAN QUALIFICATIONS AUTHORITY</b>&nbsp;</td>\r\n\t\t</tr>\r\n\t</table>\r\n\t<table width="90%" align="center" border="0">\r\n\t\t<tr>\r\n\t\t\t<td align="center"><span style="font-size: 9pt; color: #000066; font-family: Tahoma, Arial, Helvetica, sans-serif; font-weight: bold;">REGISTERED QUALIFICATION:</span>&nbsp;</td>\r\n\t\t</tr>\r\n\t</table>\r\n\t<br>\r\n\t\t<table width="90%" align="center" border="0">\r\n\t\t<tr>\r\n\t\t\t<td align="center"><b>Advanced Certificate in Facilities Maintenance Management</b>&nbsp;</td>\r\n\t\t</tr>\r\n\t</table>\r\n\t\t<table border="1" cellpadding="2" cellspacing="2" width="90%" align="center">\r\n\t\t\t<tr>\r\n\t\t\t\t<td valign="top"><b>SAQA QUAL ID</b></td>\r\n\t\t\t\t<td valign="top" colspan="4"><b>QUALIFICATION TITLE</b></td>\r\n\t\t\t</tr>\r\n\t\t\t<tr>\r\n\t\t\t\t<td valign="top">94576&nbsp;</td>\r\n\t\t\t\t<td valign="top" colspan="4">Advanced Certificate in Facilities Maintenance Management&nbsp;</td>\r\n\t\t\t</tr>\r\n\t\t\t<tr>\r\n\t\t\t\t<td valign="top" colspan="5"><b>ORIGINATOR</b></td>\r\n\t\t\t</tr>\r\n\t\t\t<tr>\r\n\t\t\t\t<td valign="top" colspan="5">Academy for Facilities Management (Pty) Ltd (A4FM)&nbsp;</td>\r\n\t\t\t</tr>\r\n\t\t\t\t\t\t\t<tr>\r\n\t\t\t\t\t<td valign="top" colspan="3"><b>PRIMARY OR DELEGATED QUALITY ASSURANCE FUNCTIONARY</b></td>\r\n\t\t\t\t\t<td valign="top" colspan="2"><b>NQF SUB-FRAMEWORK</b></td>\r\n\t\t\t\t</tr>\r\n\t\t\t\t\t\t\t\t<tr>\r\n\t\t\t\t\t<td valign="top" colspan="3">CHE - Council on Higher Education&nbsp;</td>\r\n\t\t\t\t\t<td valign="top" colspan="2">HEQSF - Higher Education Qualifications Sub-framework&nbsp;</td>\r\n\t\t\t\t</tr>\r\n\t\t\t\t\t\t<tr>\r\n\t\t\t\t<td valign="top"><b>QUALIFICATION TYPE</b></td>\r\n\t\t\t\t<td valign="top" colspan="2"><b>FIELD</b></td>\r\n\t\t\t\t<td valign="top" colspan="2"><b>SUBFIELD</b></td>\r\n\t\t\t</tr>\r\n\t\t\t<tr>\r\n\t\t\t\t<td valign="top">Advanced Certificate&nbsp;</td>\r\n\t\t\t\t<td valign="top" colspan="2">Field 03 - Business, Commerce and Management Studies&nbsp;</td>\r\n\t\t\t\t<td valign="top" colspan="2">Generic Management&nbsp;</td>\r\n\t\t\t</tr>\r\n\t\t\t<tr>\r\n\t\t\t\t<td valign="top"><b>ABET BAND</b></td>\r\n\t\t\t\t<td valign="top"><b>MINIMUM CREDITS</b></td>\r\n\t\t\t\t<td valign="top"><b>PRE-2009 NQF LEVEL</b></td>\r\n\t\t\t\t<td valign="top"><b>NQF LEVEL</b></td>\r\n\t\t\t\t<td valign="top"><b>QUAL CLASS</b></td>\r\n\t\t\t</tr>\r\n\t\t\t<tr>\t\r\n\t\t\t\t<td valign="top">Undefined&nbsp;</td>\r\n\t\t\t\t<td valign="top">130&nbsp;</td>\r\n\t\t\t\t<td valign="top">Not Applicable&nbsp;</td>\r\n\t\t\t\t<td valign="top">NQF Level 06&nbsp;</td>\r\n\t\t\t\t<td valign="top">Regular-Provider-ELOAC&nbsp;</td>\r\n\t\t\t</tr>\r\n\t\t\t\t\t\t<tr>\r\n\t\t\t\t<td valign="top" colspan="2"><b>REGISTRATION STATUS</b></td>\r\n\t\t\t\t<td valign="top"><b>SAQA DECISION NUMBER</b></td>\r\n\t\t\t\t<td valign="top"><b>REGISTRATION START DATE</b></td>\r\n\t\t\t\t<td valign="top"><b>REGISTRATION END DATE</b></td>\r\n\t\t\t</tr>\r\n\t\t\t<tr>\r\n\t\t\t\t<td valign="top" colspan="2">\r\n\t\t\t\t\t\t\t\t\t\tReregistered&nbsp;</td>\r\n\t\t\t\t<td valign="top">SAQA 06120/18&nbsp;</td>\r\n\t\t\t\t<td valign="top">2018-07-01&nbsp;</td>\r\n\t\t\t\t<td valign="top">2021-06-30&nbsp;</td>\r\n\t\t\t</tr>\r\n\t\t\t\t\t\t\t\t\t<tr>\r\n\t\t\t\t<td valign="top" colspan="2"><b>LAST DATE FOR ENROLMENT</b></td>\r\n\t\t\t\t<td valign="top" colspan="3"><b>LAST DATE FOR ACHIEVEMENT</b></td>\r\n\t\t\t</tr>\r\n\t\t\t<tr>\t\r\n\t\t\t\t<td valign="top" colspan="2">\r\n\t\t\t\t2022-06-30\t\t\t\t\t&nbsp;\r\n\t\t\t\t</td>\r\n\t\t\t\t<td valign="top" colspan="3">\r\n\t\t\t\t2025-06-30\t\t\t\t\t&nbsp;\r\n\t\t\t\t</td>\r\n\t\t\t</tr>\r\n\t\t\t\t\t</table>\r\n\t\t<br>\r\n\t\t\t<table width="90%" align="center" border="0">\r\n\t\t<tr>\r\n\t\t\t<td align="left"><span class="smallital">In all of the tables in this document, both the pre-2009 NQF Level and the NQF Level is shown. In the text (purpose statements, qualification rules, etc), any references to NQF Levels are to the pre-2009 levels unless specifically stated otherwise. </span>&nbsp;</td>\r\n\t\t</tr>\r\n\t</table>\r\n\t<table width="90%" align="center" border="0">\r\n\t\t<tr>\r\n\t\t\t<td align="left"><br>This qualification does not replace any other qualification and is not replaced by any other qualification.&nbsp;</td>\r\n\t\t</tr>\r\n\t</table>\r\n<br>\t<table width="90%" align="center" border="0">\r\n\t\t<tr>\r\n\t\t\t<td align="left"><b>PURPOSE AND RATIONALE OF THE QUALIFICATION</b>&nbsp;</td>\r\n\t\t</tr>\r\n\t</table>\r\n\t<table width="90%" align="center" border="0">\r\n\t\t<tr>\r\n\t\t\t<td align="left">Purpose:\r<br>The purpose of this qualification is to provide a student who already works in the facilities management environment with the required skills and knowledge to perform a supervisory/managerial function concerning the maintenance of the buildings. It will also provide an understanding of the relevant economic and environmental constraints. The Advanced Certificate creates an awareness of the common pitfalls in the field and provides the necessary knowledge and skills to evaluate service providers so as to ensure as far as possible that these providers are competent and reputable.\r<br>\r<br>Learners will use tools such as maintenance check lists and the relevant codes of practice and procedures in relation to the electro-mechanical, civil decisions and financial and statutory implications which will enhance their understanding of the job.\r<br>\r<br>Furthermore, the qualification will enhance the capacity of the leaners to make decisions that will boost the overall workplace productivity and even to save lives. The learner will also be able to evaluate and synthesis the common facilities managerial challenges. An operational understanding of building based technology/machinery/systems and their limitations is established in order to enhance the Facilities Manager\'s ability to undertake a holistic evaluation of any given situation and identify root causes of and solutions to problems regarding the cost-effective maintenance of a building.\r<br>\r<br>The qualification comprises a range of modules encompassing the best, tried and tested practices in the facilities environment and draws upon the expertise of academics and employers in the field.\r<br>\r<br>The Advanced Certificate in Facilities Maintenance Management at NQF Level 6 provides learners with the required competencies to advance in facilities managerial roles across economic sectors and will enable them to follow associated career opportunities nationally and internationally.\r<br>\r<br>Rationale:\r<br>The South African Facilities Management (FM) industry shows continued growth with a clear demand for well-qualified staff within both the corporate and entrepreneurial FM sector. The qualification gives priority to the needs of the South African Facilities Management industry and the advancement thereof as per the findings of the Academy\'s research conducted.\r<br>\r<br>It should be noted that the SA Facilities Management Association (SAFMA) was recognised by SAQA as a professional body with the following professional designations.\r<br>\r<br>Accredited Facilities Professional (AFP); Certified Facilities Professional (AFP); Certified Facilities Supervisor (CFS) and Certified Facilities Practitioner (CFP). Learners who complete this qualification may apply for membership with SAFMA.\r<br>\r<br>With sensitivity to international standards and local requirements we believe that we will produce dynamic leaders with the necessary facility management skills. The Advanced Certificate in Facilities Maintenance Management is a unique qualification in that the focus is on facilities maintenance issues such as civil, mechanical and electrical aspects which are essential to efficient and effective FM.\r<br>\r<br>The development of this qualification arose due to the increasing number of facilities managers, organisations needing to handle the operational aspects of the business professionally and there is the additional pressure on managers to achieve in the areas of facilities.&nbsp;</td>\r\n\t\t</tr>\r\n\t</table>\r\n<br>\t<table width="90%" align="center" border="0">\r\n\t\t<tr>\r\n\t\t\t<td align="left"><b>LEARNING ASSUMED TO BE IN PLACE AND RECOGNITION OF PRIOR LEARNING</b>&nbsp;</td>\r\n\t\t</tr>\r\n\t</table>\r\n\t<table width="90%" align="center" border="0">\r\n\t\t<tr>\r\n\t\t\t<td align="left">Recognition of Prior Learning (RPL):\r<br>The Academy selects potential students based on the entry criteria of the institution as stipulated in the Act and the Admission Policy. Should a student wish to gain advanced entry into the qualification through Recognition of Prior Learning (RPL), all of the requirements need to be met in accordance with the RPL policy.\r<br>\r<br>Entry Requirements:\r<br>The minimum admission requirement is a Higher Certificate in the appropriate field.&nbsp;</td>\r\n\t\t</tr>\r\n\t</table>\r\n<br>\t<table width="90%" align="center" border="0">\r\n\t\t<tr>\r\n\t\t\t<td align="left"><b>RECOGNISE PREVIOUS LEARNING?</b>&nbsp;</td>\r\n\t\t</tr>\r\n\t</table>\r\n\t<table width="90%" align="center" border="0">\r\n\t\t<tr>\r\n\t\t\t<td align="left">Y&nbsp;</td>\r\n\t\t</tr>\r\n\t</table>\r\n<br>\t<table width="90%" align="center" border="0">\r\n\t\t<tr>\r\n\t\t\t<td align="left"><b>QUALIFICATION RULES</b>&nbsp;</td>\r\n\t\t</tr>\r\n\t</table>\r\n\t<table width="90%" align="center" border="0">\r\n\t\t<tr>\r\n\t\t\t<td align="left">The qualification consists of compulsory and elective modules at NQF Level 6 totalling 130 Credits.\r<br>\r<br>Compulsory modules:\r<li align: center;> Management, 16 Credits.\r<li align: center;> Contract Management and SLAs, 12 Credits.\r<li align: center;> Outsourcing, 8 Credits.\r<li align: center;> Facilities Maintenance Management - Electrical, 16 Credits.\r<li align: center;> Facilities Maintenance Management - Mechanical, 16 Credits.\r<li align: center;> Facilities Maintenance Management - Finance for Facilities Maintenance, 12 Credits.\r<li align: center;> Facilities Maintenance Management - Facility Services, 16 Credits.\r<li align: center;> Work Based Learning, 26 Credits.\r<br>Total Compulsory Modules 122 Credits.\r<br>\r<br>Elective modules (select one):\r<li align: center;> Statutes, Standards and Benchmarks applicable to facilities maintenance management, 8 Credits.\r<li align: center;> Risk Management, 8 Credits.\r<br>Total Elective 8 Credits.\r<br>\r<br>Total Credits 130.&nbsp;</td>\r\n\t\t</tr>\r\n\t</table>\r\n<br>\t<table width="90%" align="center" border="0">\r\n\t\t<tr>\r\n\t\t\t<td align="left"><b>EXIT LEVEL OUTCOMES</b>&nbsp;</td>\r\n\t\t</tr>\r\n\t</table>\r\n\t<table width="90%" align="center" border="0">\r\n\t\t<tr>\r\n\t\t\t<td align="left">1. Apply management principles for the effective delivery of Facilities Maintenance Management services.\r<br>2. Apply contract management principles concerning facilities maintenance.\r<br>3. Explain the concept "Quality of Supply" and its implications for the effective functioning of electro-mechanical components and systems.\r<br>4. Explain the impact of service contracts and management on the cost-effective service delivery in the FM Environment.\r<br>5. Discuss management principles for the effective delivery of FM services.&nbsp;</td>\r\n\t\t</tr>\r\n\t</table>\r\n<br>\t<table width="90%" align="center" border="0">\r\n\t\t<tr>\r\n\t\t\t<td align="left"><b>ASSOCIATED ASSESSMENT CRITERIA</b>&nbsp;</td>\r\n\t\t</tr>\r\n\t</table>\r\n\t<table width="90%" align="center" border="0">\r\n\t\t<tr>\r\n\t\t\t<td align="left">Associated Assessment Criteria for the Level Outcomes 1:\r<li align: center;> Management of projects, stores, configuration and replenishment of consumables for facilities maintenance is explained.\r<li align: center;> The implications of ineffective and inefficient project management are analysed.\r<li align: center;> The drivers of organisational culture and its implication for effective Facilities Management (FM) in the organisation are explained.\r<li align: center;> Principles of delegation, supervision, performance evaluation and leadership are explained.\r<li align: center;> The functions and advantages of staff assessment tools are identified.\r<li align: center;> The principles of time management are explained.\r<br>\r<br>Associated Assessment Criteria for the Level Outcomes 2:\r<li align: center;> Different maintenance solutions, i.e. in-house/outsourced are compared and proposed.\r<li align: center;> Costing of different maintenance solutions is analysed, revised and proposed.\r<li align: center;> Cost-effective management of maintenance plans is developed.\r<li align: center;> Financial reporting on the maintenance management is carried out.\r<br>\r<br>Associated Assessment Criteria for the Level Outcomes 3:\r<li align: center;> Key concepts of electricity, mechanics and civils, (e.g. Volt, Watt, Amps and BTUs) are defined.\r<li align: center;> Concepts in the field of electrics, mechanics and civils associated with the relevant components, systems, machinery in the facility or buildings are identified.\r<li align: center;> Familiarity with the electro-mechanical components, systems and concepts of facilities maintenance is achieved.\r<br>\r<br>Associated Assessment Criteria for the Level Outcomes 4:\r<li align: center;> Principles of service delivery, customer satisfaction and service excellence in the FM context are explained.\r<li align: center;> The execution of maintenance plans, inclusive of cost control and financial reporting is evaluated.\r<li align: center;> Principles of service delivery and service excellence are applied.\r<li align: center;> Customer satisfaction analysed and ways to improve it are proposed.\r<li align: center;> Client and employer FM maintenance requirements are determined.\r<br>\r<br>Associated Assessment Criteria for the Level Outcomes 5:\r<li align: center;> Financial implications of facilities maintenance decisions are interpreted.\r<li align: center;> Compliance with statutory regulatory requirements in conjunction with bylaws, company policy and best practice is analysed and evaluated.\r<li align: center;> Risk and cost of compliance or non-compliance with the statutory framework for facility maintenance management is quantified.\r<li align: center;> The principles of research methodology in the Facilities Management (FM) environment are applied.\r<br>\r<br>Integrated Assessment:\r<br>Clear description of the number and types of tests/assignments/projects/case studies is communicated to the learners. Learners will be expected to complete the following formal assessments for every module:\r<br>Formative Assessments.\r<br>Assessment Type:\r<li align: center;> Pre-Contact session Assignment Self-assessed.\r<li align: center;> Test 1 Open book.\r<li align: center;> Test 2 Closed book.\r<li align: center;> Assignment Project or case study.\r<li align: center;> Work-based learning: Experiential learning:\r<br> > Logbook.\r<br> > Report Assignment.\r<br> > Time/activity schedule.\r<br>Summative Assessments.\r<li align: center;> Exam Formal Exam.\r<li align: center;> Assignment (Integrated Assignment).\r<br>During the programme the learner is expected to complete an integrated assignment that covers the full scope of Facilities Maintenance Management.&nbsp;</td>\r\n\t\t</tr>\r\n\t</table>\r\n<br>\t<table width="90%" align="center" border="0">\r\n\t\t<tr>\r\n\t\t\t<td align="left"><b>INTERNATIONAL COMPARABILITY</b>&nbsp;</td>\r\n\t\t</tr>\r\n\t</table>\r\n\t<table width="90%" align="center" border="0">\r\n\t\t<tr>\r\n\t\t\t<td align="left">International Comparability:\r<br>The HKU School of Professional and Continuing Education offers an Advanced Diploma in Facility and Property Management. The qualification aims to develop applied knowledge and skills in Facility Management for property management practitioners to effectively integrate users\' and organisations\' needs for physical assets. This qualification also provides in-depth academic training with a balanced and comprehensive scope that forms a solid foundation for further career advancement in the Facility Management discipline.\r<br>\r<br>New York University School of Professional Studies in New York offers a Certificate in Facility Management. This certificate offers a professional credential for those who seek to begin or to further their career in managing facilities for corporate or institutional owners. It is anticipated that at completion the learner will have acquired knowledge in the following:\r<li align: center;> Facility planning and development of corporate space.\r<li align: center;> Facility maintenance and repair programs.\r<li align: center;> Financial management, planning, and controls.\r<li align: center;> Project management and budgeting.\r<li align: center;> Workplace requirements to support businesses.\r<li align: center;> Environmental issues.\r<li align: center;> Office support systems and technology.\r<li align: center;> Purchasing, bidding, and negotiating.\r<br>\r<br>The School of Continuing Studies at the University of Toronto offers a Certificate in Facility Management. The Certificate in Facility Management covers a body of knowledge that includes the management of business processes, people, and technological and knowledge resources to ensure the functionality of the built environment and achieve strategic objectives for organisations.\r<br>\r<br>Griffin College in Dublin offers a Certificate in Facilities Management. The ILM Facilities Management Award course considers the context in which the facilities manager works, how relationships are built with other stakeholders, the best ways to deliver services and linking the theory with practical work experience.\r<br>\r<br>The Facilities Management Award covers:\r<li align: center;> Facilities management in context.\r<li align: center;> Health and safety at work management.\r<li align: center;> Workplace relationship management.\r<li align: center;> Workplace service delivery.\r<li align: center;> Knowledge-based assignments.\r<li align: center;> Work-based assignments.\r<br>\r<br>Conclusion:\r<br>The Advanced Certificate in Facilities Maintenance Management provides learners with an understanding of the legalities of running and maintaining buildings, budgetary components to ensure sound fiscal oversight. It is evident that the proposed qualification compares favourably with those found at international universities. As all the qualifications focus on a command and mastery of knowledge in broad and specialised areas.&nbsp;</td>\r\n\t\t</tr>\r\n\t</table>\r\n<br>\t<table width="90%" align="center" border="0">\r\n\t\t<tr>\r\n\t\t\t<td align="left"><b>ARTICULATION OPTIONS</b>&nbsp;</td>\r\n\t\t</tr>\r\n\t</table>\r\n\t<table width="90%" align="center" border="0">\r\n\t\t<tr>\r\n\t\t\t<td align="left">This qualification makes provision for articulation for both vertical and horizontal options. The articulation is possible with other qualifications in general management specialist management.\r<br>\r<br>Horizontally, articulation is possible with the following qualifications at NQF Level 6:\r<li align: center;> Advanced Certificate in Logistics Management.\r<li align: center;> Advanced Certificate in Operations Management.\r<li align: center;> Advanced Certificate in Project Management.\r<br>\r<br>Vertically, it articulates with the following qualifications at NQF Level 7:\r<li align: center;> Bachelor of Arts in Management.\r<li align: center;> Bachelor of Business in Management.\r<li align: center;> Bachelor of Commerce in Hospitality Management.\r<li align: center;> Bachelor of Public Administration in Human Resource Management.&nbsp;</td>\r\n\t\t</tr>\r\n\t</table>\r\n<br>\t<table width="90%" align="center" border="0">\r\n\t\t<tr>\r\n\t\t\t<td align="left"><b>MODERATION OPTIONS</b>&nbsp;</td>\r\n\t\t</tr>\r\n\t</table>\r\n\t<table width="90%" align="center" border="0">\r\n\t\t<tr>\r\n\t\t\t<td align="left">N/A&nbsp;</td>\r\n\t\t</tr>\r\n\t</table>\r\n<br>\t<table width="90%" align="center" border="0">\r\n\t\t<tr>\r\n\t\t\t<td align="left"><b>CRITERIA FOR THE REGISTRATION OF ASSESSORS</b>&nbsp;</td>\r\n\t\t</tr>\r\n\t</table>\r\n\t<table width="90%" align="center" border="0">\r\n\t\t<tr>\r\n\t\t\t<td align="left">N/A&nbsp;</td>\r\n\t\t</tr>\r\n\t</table>\r\n\t<table width="90%" align="center" border="0">\r\n\t\t<tr>\r\n\t\t\t<td align="left"><br /><b>REREGISTRATION HISTORY</b>&nbsp;</td>\r\n\t\t</tr>\r\n\t</table>\r\n\t<table width="90%" align="center" border="0">\r\n\t\t<tr>\r\n\t\t\t<td align="left">As per the SAQA Board decision/s at that time, this qualification was Reregistered in 2015.&nbsp;</td>\r\n\t\t</tr>\r\n\t</table>\r\n<br>\t<table width="90%" align="center" border="0">\r\n\t\t<tr>\r\n\t\t\t<td align="left"><b>NOTES</b>&nbsp;</td>\r\n\t\t</tr>\r\n\t</table>\r\n\t<table width="90%" align="center" border="0">\r\n\t\t<tr>\r\n\t\t\t<td align="left">N/A&nbsp;</td>\r\n\t\t</tr>\r\n\t</table>\r\n<br>\t<table width="90%" align="center" border="0">\r\n\t\t<tr>\r\n\t\t\t<td align="left"><b>LEARNING PROGRAMMES RECORDED AGAINST THIS QUALIFICATION:</b>&nbsp;</td>\r\n\t\t</tr>\r\n\t</table>\r\n\t<table width="90%" align="center" border="0">\r\n\t\t<tr>\r\n\t\t\t<td align="left">&nbsp;</td>\r\n\t\t</tr>\r\n\t</table>\r\n\t<table width="90%" align="center" border="0">\r\n\t\t<tr>\r\n\t\t\t<td align="left"><b>NONE</b>&nbsp;</td>\r\n\t\t</tr>\r\n\t</table>\r\n<br><br>\t<table width="90%" align="center" border="0">\r\n\t\t<tr>\r\n\t\t\t<td align="left"><b>PROVIDERS CURRENTLY ACCREDITED TO OFFER THIS QUALIFICATION:</b>&nbsp;</td>\r\n\t\t</tr>\r\n\t</table>\r\n\t<table width="90%" align="center" border="0">\r\n\t\t<tr>\r\n\t\t\t<td align="left"><span class="smallital">This information shows the current accreditations (i.e. those not past their accreditation end dates), and is the most complete record available to SAQA as of today.   Some Primary or Delegated Quality Assurance Functionaries have a lag in their recording systems for provider accreditation, in turn leading to a lag in notifying SAQA of all the providers that they have accredited to offer qualifications and unit standards, as well as any extensions to accreditation end dates.  The relevant Primary or Delegated Quality Assurance Functionary should be notified if a record appears to be missing from here. <br></span>&nbsp;</td>\r\n\t\t</tr>\r\n\t</table>\r\n\t\t<table width="80%" align="center" cellpadding="1" cellspacing="1" border="0">\r\n\t\t\t<tr>\r\n\t\t\t\t<td width="6%">1.</td>\r\n\t\t\t\t<td>Academy for Facilities Management (Pty) Ltd (A4FM)&nbsp;</td>\r\n\t\t\t</tr>\r\n\t\t\t\t\t</table><br>\t<br>\r\n\t<hr width="90%">\r\n\t\t<table style="width:95%;margin-left: auto; margin-right:auto">\r\n\t<tr>\r\n\t\t<td>\r\n\t\t\t\t\t</td>\r\n\t\t<td>\r\n\t\t\t<span class="desci">All qualifications and part qualifications registered on the National Qualifications Framework are public property. Thus the only payment that can be made for them is for service and reproduction. It is illegal to sell this material for profit. If the material is reproduced or quoted, the South African Qualifications Authority (SAQA) should be acknowledged as the source.</span>\t\t\t\r\n\t\t</td>\r\n\t</tr>\r\n</table>\r\n'    